mapper.select:
SELECT count(value5) counts,value5 FROM gov_data_element WHERE isDeleted = 0 
AND value5 is NOT NULL

	and company_id = #{companyId}


	and status = #{status}


	and class_type = #{classType}

group by value5
order by counts desc

