mapper.select:
			SELECT
				id,
				house_types houseTypes,
				info_types infoTypes,
				info_name infoName,
				information_res_id informationResId,
				infor_code inforCode,
				time_create timeCreate,
				time_modified timeModified,
				company_id companyId,
			    group_id groupId,
				department,
				stime,
				etime,
				options,
				value1,value2,value3,value4,value5,value6,value7,value8,value9,value10,
				value11,value12,value13,value14,value15,value16,value17,value18,value19,value20,value21,value22,value23,value24,value25,value26,value27,value28,value29,value30,
				isDeleted
			FROM
				gov_house_model t
			WHERE
				1 = 1
		
			and t.id=#{param.id}
				
		
			and t.house_types=#{param.houseTypes}
		
		
			and t.info_types=#{param.infoTypes}
		
		
			and t.info_name=#{param.infoName}
		
		
			and t.information_res_id=#{param.informationResId}
		
		
			and t.infor_code=#{param.inforCode}
		
		
			and t.isDeleted=#{param.isDeleted}
		
		
			and t.group_id=#{param.groupId}
		
		
			and t.company_id=#{param.companyId}
		
		ORDER BY

		
			
					${sort}
			
			
				t.time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

SELECT id, house_types houseTypes, info_types infoTypes, info_name infoName, information_res_id informationResId, infor_code inforCode, time_create timeCreate, time_modified timeModified, company_id companyId, group_id groupId, department, stime, etime, options, value1,value2,value3,value4,value5,value6,value7,value8,value9,value10, value11,value12,value13,value14,value15,value16,value17,value18,value19,value20,value21,value22,value23,value24,value25,value26,value27,value28,value29,value30, isDeleted FROM gov_house_model t WHERE 1 = 1
and t.id=#{param.id}
and t.house_types=#{param.houseTypes}
and t.info_types=#{param.infoTypes}
and t.info_name like '%${param.infoName}%'
and t.information_res_id=#{param.informationResId}
and t.infor_code=#{param.inforCode}
and t.isDeleted=#{param.isDeleted}
and t.group_id=#{param.groupId}
and t.company_id=#{param.companyId}
ORDER BY
${sort}
t.time_create
${orderBy}
desc

mapper.update:
UPDATE gov_house_model
SET isDeleted =1
WHERE id=#{id}

mapper.delete:
		DELETE FROM gov_house_model
		WHERE id in
		
			#{o.id}
