mapper.select:
		SELECT id,time_create timeCreate,time_modified timeModified,isDeleted,company_number companyNumber,company_name companyName,company_code companyCode,address,city_id cityId FROM gov_company t WHERE 1=1
		
			and t.id=#{param.id} 
		
		
			and t.company_number=#{param.companyNumber}
		
		
			and t.address like '%${param.address}%'
		
		
		
			and t.company_name=#{param.companyName}
		
		
			and t.city_id =#{param.cityId}
		
		
		
			and t.isDeleted=#{param.isDeleted}
		
		ORDER BY

		
			
					${sort}
			
			
				t.time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

		SELECT id,time_create timeCreate,time_modified timeModified,isDeleted,company_number companyNumber,company_name companyName,company_code companyCode,address,city_id cityId FROM gov_company t WHERE 1=1
		
			and t.id=#{param.id} 
		
		
			and t.company_number like '%${param.companyNumber}%'
		
		
			and t.company_name like '%${param.companyName}%'
		
		
			and t.city_id =#{param.cityId}
		
		
		
			and t.isDeleted=#{param.isDeleted}
		
		ORDER BY
		
			
					${sort}
			
			
				t.time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

mapper.update:
UPDATE gov_company
SET isDeleted=1
WHERE id=#{id}

mapper.delete:
		DELETE FROM gov_company
		WHERE id in
		
			#{o.id}
