mapper.select:
SELECT DISTINCT t.id,t.code,t.name nodeName ,t.menu_type menuType,t.open_method openMethod,t.note,t.parent,t.level_s "level",t.url,t.icon,t.list_no listNo,t.type FROM city_rbac_role_per_scope rps RIGHT JOIN city_rbac_permission t ON rps.permission_id=t.id WHERE rps.role_id IN (${id})
AND t.type=#{param.type}
AND t.menu_type=#{param.menuType}
AND t.url like '%${param.url}%'
AND t.isDeleted=0 ORDER BY t.level_s,t.list_no asc

		SELECT id,code,name nodeName ,t.menu_type menuType,t.open_method openMethod,note,parent,level_s "level",url,icon,list_no listNo,type,validity,time_create timeCreate,time_modified timeModified,isDeleted FROM city_rbac_permission t WHERE 1=1
		
			and t.level_s=#{param.level}
		
		
			and t.id=#{param.id}
		
		
			and t.type=#{param.type}
		
		
			and t.name=#{param.nodeName}
		
		
			and t.menu_type=#{param.menuType}
		
		
			and t.isDeleted=#{param.isDeleted}
		
		
			AND t.url = #{param.url}
		
		ORDER BY

		
			
					${sort}
			
			
				t.time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

		SELECT id,code,name nodeName ,t.menu_type menuType,t.open_method openMethod,note,parent,level_s "level",url,icon,list_no listNo,type,validity,time_create timeCreate,time_modified timeModified,isDeleted FROM city_rbac_permission t WHERE 1=1
		
			and t.level_s=#{param.level}
		
		
			and t.id=#{param.id}
		
		
			and t.name like '%${param.nodeName}%'
		
		
			and t.type=#{param.type}
		
		
			and t.menu_type=#{param.menuType}
		
		
			AND t.url = #{param.url}
		
		
			and t.isDeleted=#{param.isDeleted}
		
		ORDER BY
		
			
					${sort}
			
			
				t.time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

mapper.update:
UPDATE city_rbac_permission
SET isDeleted=1
WHERE id=#{id}

UPDATE city_rbac_permission
SET isDeleted=1
WHERE url = #{url} AND name = #{nodeName}

mapper.delete:
		DELETE FROM city_rbac_permission
		WHERE id in
		
			#{o.id}
