mapper.select:
SELECT FROM gov_server WHERE 1=1 and isDeleted = 0
ORDER BY
${sort}
time_create
${orderBy}
desc

		SELECT  
		FROM gov_server  WHERE 1=1 and isDeleted = 0 
		  and
        id = #{param.id,jdbcType=NUMERIC}
      
        and
        id in (SELECT DISTINCT VALUE5 FROM GOV_APPLICATION_SYSTEM WHERE VALUE5 > 0)
      
        and
        company_id = #{param.companyId,jdbcType=NUMERIC}
      
         and
        group_id = #{param.groupId,jdbcType=NUMERIC}
      
        and
        server_brand = #{param.serverBrand,jdbcType=NUMERIC}
      
        and
        server_model = #{param.serverModel,jdbcType=VARCHAR}
      
        and
        number_s = #{param.number,jdbcType=NUMERIC}
      
       and
		value2 = #{param.value2,jdbcType=VARCHAR}
		
        and
        server_type = #{param.serverType,jdbcType=NUMERIC}
      
        and
        operating_system = #{param.operatingSystem,jdbcType=NUMERIC}
      
        and
        buying_time = #{param.buyingTime,jdbcType=VARCHAR}
      
        and
        remark = #{param.remark,jdbcType=VARCHAR}
      
        and
        belong_cproom_id = #{param.belongCproomId,jdbcType=NUMERIC}
      
        and
        time_create = #{param.timeCreate,jdbcType=TIMESTAMP}
      
        and
        time_modified = #{param.timeModified,jdbcType=TIMESTAMP}
      
        and
        isDeleted = #{param.isDeleted,jdbcType=NUMERIC}
      
		ORDER BY
		
			
					${sort}
			
			
				time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

