mapper.select:
SELECT FROM gov_application_system WHERE 1=1 and isDeleted = 0
ORDER BY
${sort}
time_create
${orderBy}
desc

		SELECT  
		FROM gov_application_system  WHERE 1=1 and isDeleted = 0 
	    and
        group_id = #{param.groupId,jdbcType=NUMERIC}
      
        and
        company_id = #{param.companyId,jdbcType=NUMERIC}
      
        and
        id = #{param.id,jdbcType=NUMERIC}
      
      and
		value2 like '%${param.value2}%'
	 
	   and
		value3 = #{param.value3,jdbcType=VARCHAR}
		
	  and
		value4 = #{param.value4,jdbcType=VARCHAR}
		
		 and
		(value5 IS NOT NULL)or
		(value6 IS NOT NULL)or
		(value7 IS NOT NULL)
		
		ORDER BY
		
			
					${sort}
			
			
				time_create
			
		
		
		
			
				${orderBy}
			
			
				desc

		SELECT count(*) FROM gov_application_system t WHERE 1=1
		  and
        isDeleted = #{isDeleted,jdbcType=NUMERIC}
      
        and
        value3 = #{value3,jdbcType=VARCHAR}

