mapper.select:
SELECT t.id, t.infor_name inforName, t.infor_code inforCode, t.infor_types inforTypes, t.infor_provider inforProvider, t.infor_remark inforRemark, t.data_element_id dataElementId, t.business_id businessId, t.time_create timeCreate, t.time_modified timeModified, t.inforTypes2,inforTypes3,inforTypes4, t.isDeleted FROM gov_information_resource t,gov_information_log l where t.id=l.information_id and t.isDeleted=0
and l.account_id=#{accountId}
order by l.time_modified desc LIMIT 5

